package com.java.newqa;

public class BinHexOcttoDecimal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String bin="100";
		String oct="10";
		String hexa="F";
		System.out.println("Convert Bin to Decimal:"+Integer.parseInt(bin,2));
		System.out.println("Convert Octa to Decimal:"+Integer.parseInt(oct,8));
		System.out.println("Convert Hexa to Decimal:"+Integer.parseInt(hexa,16));

	}

}
